import { useState } from "react";
import { getToken } from "../utils/auth";

export default function CreateShipment() {
  const [form, setForm] = useState({});

  const submit = async () => {
    await fetch("http://localhost:5001/api/shipment/create", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${getToken()}`
      },
      body: JSON.stringify(form)
    });
    alert("Shipment Created");
  };

  return (
    <div className="container">
      <h2>Create Shipment</h2>
      <input placeholder="Tracking ID" onChange={e => setForm({...form, trackingId:e.target.value})} />
      <input placeholder="Sender" onChange={e => setForm({...form, sender:e.target.value})} />
      <input placeholder="Receiver" onChange={e => setForm({...form, receiver:e.target.value})} />
      <button onClick={submit}>Create</button>
    </div>
  );
}
