import { useEffect, useState } from "react";
import { getToken } from "../utils/auth";

export default function AdminDashboard() {
  const [shipments, setShipments] = useState([]);

  const load = async () => {
    const res = await fetch("http://localhost:5001/api/shipment/all", {
      headers: { Authorization: `Bearer ${getToken()}` }
    });
    setShipments(await res.json());
  };

  const update = async (id, status) => {
    await fetch(`http://localhost:5001/api/shipment/update/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${getToken()}`
      },
      body: JSON.stringify({ status })
    });
    load();
  };

  useEffect(() => { load(); }, []);

  return (
    <div className="container">
      <h2>Admin Dashboard</h2>
      {shipments.map(s => (
        <div key={s._id} className="card">
          <p><b>{s.trackingId}</b></p>
          <p>{s.sender} â†’ {s.receiver}</p>
          <select value={s.status} onChange={e => update(s._id, e.target.value)}>
            <option>In Transit</option>
            <option>Out for Delivery</option>
            <option>Delivered</option>
            <option>Cancelled</option>
          </select>
        </div>
      ))}
    </div>
  );
}
